/**
 * Asset Type Declarations
 * 
 * Allows TypeScript to recognize image imports
 */

declare module '*.png' {
  const value: any;
  export default value;
}

declare module '*.jpg' {
  const value: any;
  export default value;
}

declare module '*.jpeg' {
  const value: any;
  export default value;
}

declare module '*.gif' {
  const value: any;
  export default value;
}

declare module '*.svg' {
  const value: any;
  export default value;
}

declare module '*.webp' {
  const value: any;
  export default value;
}
